import React from 'react';
import { motion } from 'framer-motion';
import { ArrowRight, RefreshCw, CheckCircle, AlertCircle } from 'lucide-react';

const SyncVisualization = ({ nodes, syncEvents, files }) => {
  // Get active sync events
  const activeSyncs = syncEvents.filter(event => 
    event.event_type === 'file_sync_progress' && !event.processed
  );

  // Create connections between nodes for visualization
  const createNodeConnections = () => {
    const connections = [];
    nodes.forEach((nodeA, indexA) => {
      nodes.forEach((nodeB, indexB) => {
        if (indexA < indexB) {
          const hasActiveSync = activeSyncs.some(sync => 
            (sync.node_id === nodeA.node_id && 
             files.some(file => file.file_id === sync.file_id && file.owner_node === nodeB.node_id)) ||
            (sync.node_id === nodeB.node_id && 
             files.some(file => file.file_id === sync.file_id && file.owner_node === nodeA.node_id))
          );

          connections.push({
            from: nodeA,
            to: nodeB,
            isActive: hasActiveSync,
            id: `${nodeA.node_id}-${nodeB.node_id}`
          });
        }
      });
    });
    return connections;
  };

  const connections = createNodeConnections();

  return (
    <div className="bg-white rounded-lg shadow-md p-6">
      <h3 className="text-lg font-semibold text-gray-900 mb-4">
        Network Synchronization
      </h3>

      {/* Sync Statistics */}
      <div className="grid grid-cols-3 gap-4 mb-6">
        <div className="text-center p-3 bg-blue-50 rounded-lg">
          <RefreshCw className="w-8 h-8 mx-auto text-blue-500 mb-2" />
          <p className="text-sm font-medium text-blue-700">Active Syncs</p>
          <p className="text-2xl font-bold text-blue-900">{activeSyncs.length}</p>
        </div>
        <div className="text-center p-3 bg-green-50 rounded-lg">
          <CheckCircle className="w-8 h-8 mx-auto text-green-500 mb-2" />
          <p className="text-sm font-medium text-green-700">Completed</p>
          <p className="text-2xl font-bold text-green-900">
            {syncEvents.filter(e => e.processed).length}
          </p>
        </div>
        <div className="text-center p-3 bg-gray-50 rounded-lg">
          <AlertCircle className="w-8 h-8 mx-auto text-gray-500 mb-2" />
          <p className="text-sm font-medium text-gray-700">Total Files</p>
          <p className="text-2xl font-bold text-gray-900">{files.length}</p>
        </div>
      </div>

      {/* Network Visualization */}
      <div className="relative">
        <h4 className="text-md font-medium text-gray-700 mb-3">Network Topology</h4>

        {nodes.length === 0 ? (
          <div className="text-center py-8 text-gray-500">
            <p>No nodes registered</p>
            <p className="text-sm">Register nodes to see the network topology</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {/* Central Coordinator */}
            <div className="col-span-full flex justify-center mb-6">
              <div className="relative">
                <motion.div
                  initial={{ scale: 0 }}
                  animate={{ scale: 1 }}
                  className="bg-gradient-to-br from-purple-500 to-purple-600 text-white p-4 rounded-lg shadow-lg"
                >
                  <div className="text-center">
                    <div className="w-12 h-12 bg-white bg-opacity-20 rounded-lg flex items-center justify-center mx-auto mb-2">
                      <RefreshCw className="w-6 h-6" />
                    </div>
                    <p className="font-semibold">Coordinator</p>
                    <p className="text-xs opacity-90">Central Hub</p>
                  </div>
                </motion.div>

                {/* Animated pulse effect */}
                <motion.div
                  animate={{ scale: [1, 1.2, 1] }}
                  transition={{ duration: 2, repeat: Infinity }}
                  className="absolute inset-0 bg-purple-400 rounded-lg opacity-20"
                />
              </div>
            </div>

            {/* Node Cards with Connections */}
            {nodes.map((node, index) => (
              <motion.div
                key={node.node_id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: index * 0.1 }}
                className="relative"
              >
                {/* Connection Line to Coordinator */}
                <div className="absolute -top-16 left-1/2 transform -translate-x-1/2 w-px bg-gray-300 h-16">
                  {activeSyncs.some(sync => sync.node_id === node.node_id) && (
                    <motion.div
                      animate={{ y: [0, 64, 0] }}
                      transition={{ duration: 1.5, repeat: Infinity }}
                      className="w-2 h-2 bg-blue-500 rounded-full absolute left-1/2 transform -translate-x-1/2"
                    />
                  )}
                </div>

                {/* Node Card */}
                <div className={`p-4 rounded-lg border-2 transition-all duration-300 ${
                  node.status === 'online' 
                    ? 'bg-green-50 border-green-300' 
                    : node.status === 'syncing'
                    ? 'bg-blue-50 border-blue-300 animate-pulse'
                    : 'bg-red-50 border-red-300'
                }`}>
                  <div className="text-center">
                    <div className={`w-10 h-10 mx-auto rounded-lg flex items-center justify-center mb-2 ${
                      node.status === 'online' ? 'bg-green-100' : 
                      node.status === 'syncing' ? 'bg-blue-100' : 'bg-red-100'
                    }`}>
                      <div className={`w-3 h-3 rounded-full ${
                        node.status === 'online' ? 'bg-green-500' : 
                        node.status === 'syncing' ? 'bg-blue-500' : 'bg-red-500'
                      }`} />
                    </div>
                    <p className="font-medium text-sm">{node.name || node.node_id}</p>
                    <p className="text-xs text-gray-500">{node.status}</p>

                    {/* File count for this node */}
                    <div className="mt-2">
                      <span className="inline-flex items-center px-2 py-1 rounded-full text-xs bg-gray-100 text-gray-800">
                        {files.filter(f => f.owner_node === node.node_id).length} files
                      </span>
                    </div>
                  </div>
                </div>
              </motion.div>
            ))}
          </div>
        )}

        {/* Active Sync Events */}
        {activeSyncs.length > 0 && (
          <div className="mt-6">
            <h4 className="text-md font-medium text-gray-700 mb-3">Active Synchronizations</h4>
            <div className="space-y-2">
              {activeSyncs.slice(0, 5).map((sync) => {
                const file = files.find(f => f.file_id === sync.file_id);
                return (
                  <motion.div
                    key={sync.event_id}
                    initial={{ opacity: 0, x: -20 }}
                    animate={{ opacity: 1, x: 0 }}
                    className="flex items-center justify-between p-3 bg-blue-50 rounded-lg border border-blue-200"
                  >
                    <div className="flex items-center space-x-3">
                      <motion.div
                        animate={{ rotate: 360 }}
                        transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
                      >
                        <RefreshCw className="w-4 h-4 text-blue-500" />
                      </motion.div>
                      <div>
                        <p className="text-sm font-medium text-gray-900">
                          {file?.name || 'Unknown file'}
                        </p>
                        <p className="text-xs text-gray-500">
                          Node: {sync.node_id}
                        </p>
                      </div>
                    </div>
                    <div className="text-right">
                      <p className="text-xs text-blue-600 font-medium">Syncing...</p>
                      {sync.data?.bandwidth_saved && (
                        <p className="text-xs text-gray-500">
                          Saved: {Math.round(sync.data.bandwidth_saved / 1024)} KB
                        </p>
                      )}
                    </div>
                  </motion.div>
                );
              })}
            </div>
          </div>
        )}

        {/* Recent Events */}
        <div className="mt-6">
          <h4 className="text-md font-medium text-gray-700 mb-3">Recent Events</h4>
          <div className="max-h-40 overflow-y-auto space-y-1">
            {syncEvents.slice(0, 10).map((event) => (
              <div key={event.event_id} className="flex items-center justify-between p-2 text-xs bg-gray-50 rounded">
                <span className="text-gray-600">
                  {event.event_type.replace('_', ' ').toUpperCase()}
                </span>
                <span className="text-gray-500">
                  {new Date(event.timestamp).toLocaleTimeString()}
                </span>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default SyncVisualization;
