import React, { useState, useEffect, useMemo } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Server, 
  RefreshCw, 
  ArrowRight,
  CheckCircle,
  AlertCircle,
  FileText,
  Users,
  Activity,
  Zap
} from 'lucide-react';

const SyncVisualization = ({ nodes, syncEvents, files }) => {
  const [activeSyncs, setActiveSyncs] = useState(new Map());
  const [recentActivity, setRecentActivity] = useState([]);

  // Process sync events to track active synchronizations
  useEffect(() => {
    if (!syncEvents || syncEvents.length === 0) return;

    const newActiveSyncs = new Map(activeSyncs);
    const newActivity = [...recentActivity];

    syncEvents.forEach((event) => {
      const eventKey = `${event.file_id}-${event.node_id}`;

      if (event.event_type === 'file_sync_progress') {
        const data = event.data || {};

        if (data.action === 'sync_started') {
          newActiveSyncs.set(eventKey, {
            fileId: event.file_id,
            fileName: data.file_name,
            sourceNode: data.source_node,
            targetNode: data.target_node,
            progress: 0,
            startTime: new Date(event.timestamp),
            status: 'syncing'
          });
        } else if (data.action === 'syncing') {
          const existing = newActiveSyncs.get(eventKey);
          if (existing) {
            newActiveSyncs.set(eventKey, {
              ...existing,
              progress: data.progress || 0
            });
          }
        }
      } else if (event.event_type === 'sync_completed') {
        const existing = newActiveSyncs.get(eventKey);
        if (existing) {
          newActiveSyncs.set(eventKey, {
            ...existing,
            progress: 100,
            status: 'completed',
            completedTime: new Date(event.timestamp)
          });

          // Add to recent activity
          newActivity.unshift({
            id: event.event_id,
            type: 'sync_completed',
            fileName: event.data?.file_name || 'Unknown',
            sourceNode: event.data?.source_node,
            targetNode: event.data?.target_node,
            timestamp: new Date(event.timestamp),
            bytesTransferred: event.data?.bytes_transferred
          });

          // Remove completed sync after 3 seconds
          setTimeout(() => {
            setActiveSyncs(prev => {
              const updated = new Map(prev);
              updated.delete(eventKey);
              return updated;
            });
          }, 3000);
        }
      } else if (event.event_type === 'file_modified' && event.data?.action === 'uploaded') {
        // Add upload activity
        newActivity.unshift({
          id: event.event_id,
          type: 'file_uploaded',
          fileName: event.data?.file_name || 'Unknown',
          sourceNode: event.node_id,
          timestamp: new Date(event.timestamp),
          fileSize: event.data?.file_size
        });
      }
    });

    setActiveSyncs(newActiveSyncs);
    setRecentActivity(newActivity.slice(0, 20)); // Keep only recent 20 activities
  }, [syncEvents]);

  // Calculate network topology for visualization
  const networkTopology = useMemo(() => {
    const onlineNodes = nodes.filter(node => node.status === 'online');
    const radius = 150;
    const centerX = 200;
    const centerY = 200;

    return onlineNodes.map((node, index) => {
      const angle = (index * 2 * Math.PI) / onlineNodes.length;
      const x = centerX + radius * Math.cos(angle);
      const y = centerY + radius * Math.sin(angle);

      return {
        ...node,
        x,
        y,
        fileCount: files.filter(f => f.owner_node === node.node_id).length
      };
    });
  }, [nodes, files]);

  const formatFileSize = (bytes) => {
    if (!bytes) return '0 B';
    const k = 1024;
    const sizes = ['B', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(1)) + ' ' + sizes[i];
  };

  const getNodeColor = (node) => {
    if (node.status === 'online') return '#10b981'; // green
    if (node.status === 'syncing') return '#3b82f6'; // blue
    return '#6b7280'; // gray
  };

  const SyncConnection = ({ sync }) => {
    const sourceNode = networkTopology.find(n => n.node_id === sync.sourceNode);
    const targetNode = networkTopology.find(n => n.node_id === sync.targetNode);

    if (!sourceNode || !targetNode) return null;

    const progressOffset = 1 - (sync.progress / 100);

    return (
      <g key={`${sync.fileId}-${sync.targetNode}`}>
        {/* Connection line */}
        <line
          x1={sourceNode.x}
          y1={sourceNode.y}
          x2={targetNode.x}
          y2={targetNode.y}
          stroke="#3b82f6"
          strokeWidth="2"
          strokeDasharray="5,5"
          className="animate-pulse"
        />

        {/* Progress indicator */}
        <motion.circle
          cx={sourceNode.x + (targetNode.x - sourceNode.x) * (sync.progress / 100)}
          cy={sourceNode.y + (targetNode.y - sourceNode.y) * (sync.progress / 100)}
          r="4"
          fill="#3b82f6"
          initial={{ scale: 0 }}
          animate={{ scale: 1 }}
          transition={{ duration: 0.3 }}
        />

        {/* File icon moving along path */}
        <motion.g
          initial={{ 
            x: sourceNode.x, 
            y: sourceNode.y 
          }}
          animate={{ 
            x: sourceNode.x + (targetNode.x - sourceNode.x) * (sync.progress / 100),
            y: sourceNode.y + (targetNode.y - sourceNode.y) * (sync.progress / 100)
          }}
          transition={{ duration: 0.5, ease: "easeOut" }}
        >
          <FileText size={12} fill="#ffffff" stroke="#3b82f6" />
        </motion.g>
      </g>
    );
  };

  return (
    <div className="bg-white rounded-lg shadow-md p-6">
      <div className="flex items-center justify-between mb-6">
        <h2 className="text-xl font-semibold text-gray-900">Network Synchronization</h2>
        <div className="flex items-center space-x-4 text-sm">
          <div className="flex items-center space-x-2">
            <div className="w-3 h-3 bg-green-500 rounded-full"></div>
            <span className="text-gray-600">Online</span>
          </div>
          <div className="flex items-center space-x-2">
            <div className="w-3 h-3 bg-blue-500 rounded-full animate-pulse"></div>
            <span className="text-gray-600">Syncing</span>
          </div>
          <div className="flex items-center space-x-2">
            <Activity className="w-4 h-4 text-gray-500" />
            <span className="text-gray-600">{activeSyncs.size} Active</span>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Network Topology Visualization */}
        <div className="bg-gray-50 rounded-lg p-4">
          <h3 className="text-lg font-medium text-gray-900 mb-4">Network Topology</h3>

          {networkTopology.length === 0 ? (
            <div className="text-center py-12">
              <Users className="w-12 h-12 mx-auto text-gray-400 mb-4" />
              <p className="text-gray-500">No nodes online</p>
            </div>
          ) : (
            <div className="relative">
              <svg width="400" height="400" className="mx-auto">
                {/* Node connections during sync */}
                <AnimatePresence>
                  {Array.from(activeSyncs.values()).map((sync) => (
                    <SyncConnection key={`${sync.fileId}-${sync.targetNode}`} sync={sync} />
                  ))}
                </AnimatePresence>

                {/* Nodes */}
                {networkTopology.map((node) => (
                  <g key={node.node_id}>
                    {/* Node circle */}
                    <motion.circle
                      cx={node.x}
                      cy={node.y}
                      r="20"
                      fill={getNodeColor(node)}
                      stroke="#ffffff"
                      strokeWidth="3"
                      initial={{ scale: 0 }}
                      animate={{ scale: 1 }}
                      transition={{ duration: 0.5 }}
                      className={node.status === 'online' ? 'drop-shadow-lg' : ''}
                    />

                    {/* Node icon */}
                    <foreignObject
                      x={node.x - 8}
                      y={node.y - 8}
                      width="16"
                      height="16"
                    >
                      <Server className="w-4 h-4 text-white" />
                    </foreignObject>

                    {/* Node label */}
                    <text
                      x={node.x}
                      y={node.y + 35}
                      textAnchor="middle"
                      className="text-xs font-medium fill-gray-700"
                    >
                      {node.node_id.slice(0, 8)}...
                    </text>

                    {/* File count badge */}
                    {node.fileCount > 0 && (
                      <g>
                        <circle
                          cx={node.x + 15}
                          cy={node.y - 15}
                          r="8"
                          fill="#ef4444"
                          stroke="#ffffff"
                          strokeWidth="2"
                        />
                        <text
                          x={node.x + 15}
                          y={node.y - 11}
                          textAnchor="middle"
                          className="text-xs font-bold fill-white"
                        >
                          {node.fileCount}
                        </text>
                      </g>
                    )}
                  </g>
                ))}
              </svg>
            </div>
          )}
        </div>

        {/* Activity Feed */}
        <div className="bg-gray-50 rounded-lg p-4">
          <h3 className="text-lg font-medium text-gray-900 mb-4">Recent Activity</h3>

          <div className="space-y-3 max-h-80 overflow-y-auto">
            <AnimatePresence>
              {activeSyncs.size > 0 && (
                <div className="space-y-2">
                  {Array.from(activeSyncs.values()).map((sync) => (
                    <motion.div
                      key={`${sync.fileId}-${sync.targetNode}`}
                      initial={{ opacity: 0, y: -10 }}
                      animate={{ opacity: 1, y: 0 }}
                      exit={{ opacity: 0, x: 10 }}
                      className="bg-blue-100 border border-blue-300 rounded-lg p-3"
                    >
                      <div className="flex items-center justify-between mb-2">
                        <div className="flex items-center space-x-2">
                          <motion.div
                            animate={{ rotate: 360 }}
                            transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
                          >
                            <RefreshCw className="w-4 h-4 text-blue-600" />
                          </motion.div>
                          <span className="text-sm font-medium text-blue-900">
                            Syncing {sync.fileName}
                          </span>
                        </div>
                        <span className="text-xs text-blue-700">
                          {sync.progress}%
                        </span>
                      </div>

                      <div className="flex items-center space-x-2 text-xs text-blue-700 mb-2">
                        <span>{sync.sourceNode.slice(0, 8)}...</span>
                        <ArrowRight className="w-3 h-3" />
                        <span>{sync.targetNode.slice(0, 8)}...</span>
                      </div>

                      <div className="w-full bg-blue-200 rounded-full h-2">
                        <motion.div
                          className="bg-blue-600 h-2 rounded-full"
                          initial={{ width: "0%" }}
                          animate={{ width: `${sync.progress}%` }}
                          transition={{ duration: 0.3 }}
                        />
                      </div>
                    </motion.div>
                  ))}
                </div>
              )}

              {recentActivity.map((activity) => (
                <motion.div
                  key={activity.id}
                  initial={{ opacity: 0, y: -10 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, x: 10 }}
                  className={`rounded-lg p-3 ${
                    activity.type === 'sync_completed' 
                      ? 'bg-green-100 border border-green-300' 
                      : 'bg-gray-100 border border-gray-300'
                  }`}
                >
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-2">
                      {activity.type === 'sync_completed' ? (
                        <CheckCircle className="w-4 h-4 text-green-600" />
                      ) : (
                        <FileText className="w-4 h-4 text-gray-600" />
                      )}
                      <span className="text-sm font-medium text-gray-900">
                        {activity.fileName}
                      </span>
                    </div>
                    <span className="text-xs text-gray-500">
                      {activity.timestamp.toLocaleTimeString()}
                    </span>
                  </div>

                  <div className="mt-1 text-xs text-gray-600">
                    {activity.type === 'sync_completed' ? (
                      <div className="flex items-center space-x-2">
                        <span>{activity.sourceNode?.slice(0, 8)}...</span>
                        <ArrowRight className="w-3 h-3" />
                        <span>{activity.targetNode?.slice(0, 8)}...</span>
                        {activity.bytesTransferred && (
                          <span className="text-green-600">
                            • {formatFileSize(activity.bytesTransferred)}
                          </span>
                        )}
                      </div>
                    ) : (
                      <div>
                        Uploaded to {activity.sourceNode?.slice(0, 8)}...
                        {activity.fileSize && (
                          <span className="text-gray-500">
                            • {formatFileSize(activity.fileSize)}
                          </span>
                        )}
                      </div>
                    )}
                  </div>
                </motion.div>
              ))}
            </AnimatePresence>

            {recentActivity.length === 0 && activeSyncs.size === 0 && (
              <div className="text-center py-8">
                <Activity className="w-8 h-8 mx-auto text-gray-400 mb-2" />
                <p className="text-gray-500 text-sm">No recent activity</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default SyncVisualization;
