import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Plus, Users, Settings } from 'lucide-react';
import { nodeApi } from '../utils/api';

const NodeManager = ({ nodes, onNodeAdded, onRefresh }) => {
  const [isAddingNode, setIsAddingNode] = useState(false);
  const [newNode, setNewNode] = useState({
    node_id: '',
    name: '',
    address: 'localhost',
    port: 9001,
    watch_directories: ['/tmp/node_files'],
    capabilities: ['file_sync', 'delta_sync']
  });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const handleAddNode = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    try {
      const result = await nodeApi.register(newNode);
      if (onNodeAdded) {
        onNodeAdded(result);
      }

      // Reset form
      setNewNode({
        node_id: '',
        name: '',
        address: 'localhost',
        port: 9001,
        watch_directories: ['/tmp/node_files'],
        capabilities: ['file_sync', 'delta_sync']
      });
      setIsAddingNode(false);

      // Refresh nodes list
      if (onRefresh) {
        onRefresh();
      }
    } catch (error) {
      setError(error.response?.data?.detail || error.message || 'Failed to register node');
    } finally {
      setIsLoading(false);
    }
  };

  const generateNodeId = () => {
    const timestamp = Date.now();
    const random = Math.random().toString(36).substring(2, 7);
    return `node_${timestamp}_${random}`;
  };

  return (
    <div className="bg-white rounded-lg shadow-md p-6">
      <div className="flex items-center justify-between mb-6">
        <div className="flex items-center space-x-3">
          <Users className="w-6 h-6 text-gray-600" />
          <h3 className="text-lg font-semibold text-gray-900">Node Management</h3>
        </div>
        <button
          onClick={() => {
            setIsAddingNode(!isAddingNode);
            if (!isAddingNode) {
              setNewNode(prev => ({
                ...prev,
                node_id: generateNodeId(),
                port: 9001 + nodes.length
              }));
            }
          }}
          className="flex items-center space-x-2 px-4 py-2 bg-primary-600 text-white rounded-lg hover:bg-primary-700 transition-colors"
        >
          <Plus className="w-4 h-4" />
          <span>Add Node</span>
        </button>
      </div>

      {/* Add Node Form */}
      {isAddingNode && (
        <motion.div
          initial={{ opacity: 0, height: 0 }}
          animate={{ opacity: 1, height: 'auto' }}
          exit={{ opacity: 0, height: 0 }}
          className="mb-6 p-4 border border-gray-200 rounded-lg bg-gray-50"
        >
          <h4 className="text-md font-medium text-gray-900 mb-4">Register New Node</h4>

          {error && (
            <div className="mb-4 p-3 bg-red-50 border border-red-200 rounded-lg">
              <p className="text-sm text-red-700">{error}</p>
            </div>
          )}

          <form onSubmit={handleAddNode} className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Node ID
                </label>
                <input
                  type="text"
                  value={newNode.node_id}
                  onChange={(e) => setNewNode(prev => ({...prev, node_id: e.target.value}))}
                  className="w-full p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-primary-500"
                  placeholder="unique_node_id"
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Display Name
                </label>
                <input
                  type="text"
                  value={newNode.name}
                  onChange={(e) => setNewNode(prev => ({...prev, name: e.target.value}))}
                  className="w-full p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-primary-500"
                  placeholder="My Node"
                  required
                />
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Address
                </label>
                <input
                  type="text"
                  value={newNode.address}
                  onChange={(e) => setNewNode(prev => ({...prev, address: e.target.value}))}
                  className="w-full p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-primary-500"
                  placeholder="localhost"
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Port
                </label>
                <input
                  type="number"
                  value={newNode.port}
                  onChange={(e) => setNewNode(prev => ({...prev, port: parseInt(e.target.value)}))}
                  className="w-full p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-primary-500"
                  placeholder="9001"
                  min="1"
                  max="65535"
                  required
                />
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Watch Directories (comma-separated)
              </label>
              <input
                type="text"
                value={newNode.watch_directories.join(', ')}
                onChange={(e) => setNewNode(prev => ({
                  ...prev, 
                  watch_directories: e.target.value.split(',').map(s => s.trim()).filter(Boolean)
                }))}
                className="w-full p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-primary-500"
                placeholder="/tmp/node_files, /home/user/sync"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Capabilities
              </label>
              <div className="flex flex-wrap gap-2">
                {['file_sync', 'delta_sync', 'conflict_resolution', 'real_time_sync'].map(capability => (
                  <label key={capability} className="flex items-center space-x-2">
                    <input
                      type="checkbox"
                      checked={newNode.capabilities.includes(capability)}
                      onChange={(e) => {
                        if (e.target.checked) {
                          setNewNode(prev => ({
                            ...prev,
                            capabilities: [...prev.capabilities, capability]
                          }));
                        } else {
                          setNewNode(prev => ({
                            ...prev,
                            capabilities: prev.capabilities.filter(c => c !== capability)
                          }));
                        }
                      }}
                      className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                    />
                    <span className="text-sm text-gray-700">{capability}</span>
                  </label>
                ))}
              </div>
            </div>

            <div className="flex justify-end space-x-3">
              <button
                type="button"
                onClick={() => setIsAddingNode(false)}
                className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors"
              >
                Cancel
              </button>
              <button
                type="submit"
                disabled={isLoading}
                className="px-4 py-2 text-sm font-medium text-white bg-primary-600 rounded-lg hover:bg-primary-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
              >
                {isLoading ? 'Registering...' : 'Register Node'}
              </button>
            </div>
          </form>
        </motion.div>
      )}

      {/* Nodes List */}
      <div>
        <h4 className="text-md font-medium text-gray-900 mb-3">
          Registered Nodes ({nodes.length})
        </h4>

        {nodes.length === 0 ? (
          <div className="text-center py-8 text-gray-500">
            <Users className="w-12 h-12 mx-auto text-gray-300 mb-3" />
            <p>No nodes registered</p>
            <p className="text-sm">Add your first node to get started</p>
          </div>
        ) : (
          <div className="space-y-3">
            {nodes.map((node) => (
              <motion.div
                key={node.node_id}
                initial={{ opacity: 0, y: 10 }}
                animate={{ opacity: 1, y: 0 }}
                className={`p-4 rounded-lg border-2 transition-all duration-200 ${
                  node.status === 'online' 
                    ? 'bg-green-50 border-green-200' 
                    : node.status === 'syncing'
                    ? 'bg-blue-50 border-blue-200'
                    : 'bg-red-50 border-red-200'
                }`}
              >
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <div className={`w-3 h-3 rounded-full ${
                      node.status === 'online' ? 'bg-green-500' :
                      node.status === 'syncing' ? 'bg-blue-500' : 'bg-red-500'
                    }`} />
                    <div>
                      <h5 className="font-medium text-gray-900">
                        {node.name || node.node_id}
                      </h5>
                      <p className="text-sm text-gray-500">
                        {node.address}:{node.port} â€¢ {node.status}
                      </p>
                    </div>
                  </div>

                  <div className="flex items-center space-x-2">
                    <span className="text-xs text-gray-500">
                      {node.file_count || 0} files
                    </span>
                    <button className="p-1 text-gray-400 hover:text-gray-600 transition-colors">
                      <Settings className="w-4 h-4" />
                    </button>
                  </div>
                </div>

                {/* Capabilities */}
                {node.capabilities && node.capabilities.length > 0 && (
                  <div className="mt-3 flex flex-wrap gap-1">
                    {node.capabilities.map((capability) => (
                      <span
                        key={capability}
                        className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-gray-100 text-gray-800"
                      >
                        {capability}
                      </span>
                    ))}
                  </div>
                )}

                {/* Watch Directories */}
                {node.watch_directories && node.watch_directories.length > 0 && (
                  <div className="mt-2">
                    <p className="text-xs text-gray-500">
                      Watching: {node.watch_directories.join(', ')}
                    </p>
                  </div>
                )}
              </motion.div>
            ))}
          </div>
        )}
      </div>

      {/* Quick Stats */}
      <div className="mt-6 pt-4 border-t border-gray-200">
        <div className="grid grid-cols-3 gap-4 text-center">
          <div>
            <p className="text-2xl font-bold text-green-600">
              {nodes.filter(n => n.status === 'online').length}
            </p>
            <p className="text-xs text-gray-500">Online</p>
          </div>
          <div>
            <p className="text-2xl font-bold text-blue-600">
              {nodes.filter(n => n.status === 'syncing').length}
            </p>
            <p className="text-xs text-gray-500">Syncing</p>
          </div>
          <div>
            <p className="text-2xl font-bold text-red-600">
              {nodes.filter(n => n.status === 'offline').length}
            </p>
            <p className="text-xs text-gray-500">Offline</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default NodeManager;
