import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { 
  Plus, 
  Server, 
  Trash2, 
  AlertCircle,
  CheckCircle,
  Loader
} from 'lucide-react';
import { nodeApi } from '../utils/api';

const NodeManager = ({ nodes, onNodeAdded, onNodeRemoved }) => {
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    node_id: '',
    name: '',
    address: 'localhost',
    port: 8001,
    watch_directories: ['/tmp/sync']
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setError('');

    try {
      const response = await nodeApi.register({
        ...formData,
        capabilities: ['sync', 'upload', 'download']
      });

      if (onNodeAdded) {
        onNodeAdded(response);
      }

      // Reset form
      setFormData({
        node_id: '',
        name: '',
        address: 'localhost',
        port: 8001,
        watch_directories: ['/tmp/sync']
      });
      setShowForm(false);
    } catch (error) {
      setError(error.response?.data?.detail || error.message);
    } finally {
      setIsSubmitting(false);
    }
  };

  const addTestNode = async () => {
    setIsSubmitting(true);
    setError('');

    try {
      const testNodeData = {
        node_id: `test_node_${Date.now()}`,
        name: `Test Node ${Math.floor(Math.random() * 100)}`,
        address: 'localhost',
        port: 8001 + Math.floor(Math.random() * 100),
        watch_directories: ['/tmp/sync'],
        capabilities: ['sync', 'upload', 'download']
      };

      const response = await nodeApi.register(testNodeData);

      if (onNodeAdded) {
        onNodeAdded(response);
      }
    } catch (error) {
      setError(error.response?.data?.detail || error.message);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: name === 'port' ? parseInt(value) : value
    }));
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <h2 className="text-2xl font-bold text-gray-900">Node Management</h2>
        <div className="flex space-x-3">
          <button
            onClick={addTestNode}
            disabled={isSubmitting}
            className="px-4 py-2 text-sm font-medium text-white bg-green-600 rounded-lg hover:bg-green-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors flex items-center space-x-2"
          >
            {isSubmitting && <Loader className="w-4 h-4 animate-spin" />}
            <Plus className="w-4 h-4" />
            <span>Add Test Node</span>
          </button>
          <button
            onClick={() => setShowForm(!showForm)}
            className="px-4 py-2 text-sm font-medium text-white bg-primary-600 rounded-lg hover:bg-primary-700 transition-colors flex items-center space-x-2"
          >
            <Plus className="w-4 h-4" />
            <span>Add Node</span>
          </button>
        </div>
      </div>

      {/* Error Message */}
      {error && (
        <motion.div
          initial={{ opacity: 0, y: -10 }}
          animate={{ opacity: 1, y: 0 }}
          className="flex items-center space-x-2 p-3 bg-red-50 border border-red-200 rounded-lg"
        >
          <AlertCircle className="w-5 h-5 text-red-500 flex-shrink-0" />
          <span className="text-sm text-red-700">{error}</span>
        </motion.div>
      )}

      {/* Add Node Form */}
      {showForm && (
        <motion.form
          initial={{ opacity: 0, height: 0 }}
          animate={{ opacity: 1, height: 'auto' }}
          exit={{ opacity: 0, height: 0 }}
          onSubmit={handleSubmit}
          className="bg-gray-50 rounded-lg p-6 space-y-4"
        >
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Node ID
              </label>
              <input
                type="text"
                name="node_id"
                value={formData.node_id}
                onChange={handleInputChange}
                required
                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-primary-500"
                placeholder="node_001"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Node Name
              </label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                required
                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-primary-500"
                placeholder="My Sync Node"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Address
              </label>
              <input
                type="text"
                name="address"
                value={formData.address}
                onChange={handleInputChange}
                required
                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-primary-500"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Port
              </label>
              <input
                type="number"
                name="port"
                value={formData.port}
                onChange={handleInputChange}
                required
                min="1"
                max="65535"
                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-primary-500"
              />
            </div>
          </div>

          <div className="flex justify-end space-x-3">
            <button
              type="button"
              onClick={() => setShowForm(false)}
              className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors"
            >
              Cancel
            </button>
            <button
              type="submit"
              disabled={isSubmitting}
              className="px-4 py-2 text-sm font-medium text-white bg-primary-600 rounded-lg hover:bg-primary-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors flex items-center space-x-2"
            >
              {isSubmitting && <Loader className="w-4 h-4 animate-spin" />}
              <span>Register Node</span>
            </button>
          </div>
        </motion.form>
      )}

      {/* Nodes List */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {nodes.map((node) => (
          <motion.div
            key={node.node_id}
            initial={{ opacity: 0, scale: 0.95 }}
            animate={{ opacity: 1, scale: 1 }}
            className="bg-white rounded-lg shadow-md border border-gray-200 overflow-hidden"
          >
            <div className="p-4">
              <div className="flex items-center justify-between mb-3">
                <div className="flex items-center space-x-2">
                  <Server className="w-5 h-5 text-gray-500" />
                  <span className="font-medium text-gray-900 truncate">
                    {node.name || node.node_id}
                  </span>
                </div>
                <div className={`flex items-center space-x-1 px-2 py-1 rounded-full text-xs font-medium ${
                  node.status === 'online' 
                    ? 'bg-green-100 text-green-800'
                    : 'bg-red-100 text-red-800'
                }`}>
                  <div className={`w-2 h-2 rounded-full ${
                    node.status === 'online' ? 'bg-green-500' : 'bg-red-500'
                  }`} />
                  <span>{node.status}</span>
                </div>
              </div>

              <div className="space-y-1 text-sm text-gray-600">
                <p>
                  <span className="font-medium">Address:</span> {node.address}:{node.port}
                </p>
                <p>
                  <span className="font-medium">Files:</span> {node.file_count || 0}
                </p>
                <p>
                  <span className="font-medium">Last seen:</span>{' '}
                  {node.last_seen ? new Date(node.last_seen).toLocaleString() : 'Never'}
                </p>
              </div>
            </div>
          </motion.div>
        ))}

        {nodes.length === 0 && (
          <div className="col-span-full text-center py-12">
            <Server className="w-12 h-12 mx-auto text-gray-400 mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">No nodes registered</h3>
            <p className="text-gray-500 mb-4">Register your first node to start synchronizing files</p>
            <button
              onClick={addTestNode}
              disabled={isSubmitting}
              className="px-4 py-2 text-sm font-medium text-white bg-primary-600 rounded-lg hover:bg-primary-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors flex items-center space-x-2 mx-auto"
            >
              {isSubmitting && <Loader className="w-4 h-4 animate-spin" />}
              <Plus className="w-4 h-4" />
              <span>Add Test Node</span>
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default NodeManager;
